function updateClock() {
    const clockElement = document.getElementById("clock");
    const now = new Date();
    const hours = now.getHours().toString().padStart(2, "0");
    const minutes = now.getMinutes().toString().padStart(2, "0");
    const seconds = now.getSeconds().toString().padStart(2, "0");
    const timeString = `${hours}:${minutes}:${seconds}`;
    clockElement.textContent = timeString;

    setTimeout(updateClock, 1000);
}

function startTimer() {
    const timerElement = document.getElementById("timer");
    let startTime = new Date().getTime();

    function updateTimer() {
        const currentTime = new Date().getTime();
        const elapsedTime = new Date(currentTime - startTime);

        const hours = elapsedTime.getUTCHours().toString().padStart(2, "0");
        const minutes = elapsedTime.getUTCMinutes().toString().padStart(2, "0");
        const seconds = elapsedTime.getUTCSeconds().toString().padStart(2, "0");

        const timerString = `Timer: ${hours}:${minutes}:${seconds}`;
        timerElement.textContent = timerString;

        setTimeout(updateTimer, 1000);
    }

    updateTimer();
}

function shuffleArray(arrayOrObject) {
    if (Array.isArray(arrayOrObject)) {
        // Shuffle an array
        for (let i = arrayOrObject.length - 1; i > 0; i--) {
            const j = Math.floor(Math.random() * (i + 1));
            [arrayOrObject[i], arrayOrObject[j]] = [arrayOrObject[j], arrayOrObject[i]];
        }
    } else if (typeof arrayOrObject === 'object') {
        // Shuffle an object (questions)
        const keys = Object.keys(arrayOrObject);
        for (let i = keys.length - 1; i > 0; i--) {
            const j = Math.floor(Math.random() * (i + 1));
            [arrayOrObject[keys[i]], arrayOrObject[keys[j]]] = [arrayOrObject[keys[j]], arrayOrObject[keys[i]]];
        }
    }

    return arrayOrObject;
}
