let selectedSubject;

function getQuestions(subject, chapter, marks) {
    const subjectData = questions[subject];
    if (subjectData && subjectData[chapter] && subjectData[chapter].questions) {
        shuffledQuestions = shuffleArray(subjectData[chapter].questions.slice(0, marks));
        selectedSubject = subject;  // Assign the value to the global variable
        selectedChapter = chapter;
        selectedMarks = marks;
        return shuffledQuestions;
    } else {
        console.error(`Questions not found for subject: ${subject}, chapter: ${chapter}`);
        return [];
    }
}

// ... (your existing code)

function displayQuestions(questionsToDisplay) {
    const questionsContainer = document.getElementById("quiz-content");

    questionsToDisplay.forEach((questionObj, index) => {
        const questionElement = document.createElement("div");
        questionElement.classList.add("question");

        const questionText = document.createElement("p");
        questionText.textContent = `Question ${index + 1}: ${questionObj.question}`;
        questionText.style.fontWeight = "bold";  // Make question text bold
        questionElement.appendChild(questionText);

        questionObj.options.forEach((option, optionIndex) => {
            const optionInput = document.createElement("input");
            optionInput.type = "radio";
            optionInput.name = `question-${index + 1}-options`;
            optionInput.value = option;

            const optionLabel = document.createElement("label");
            optionLabel.textContent = option;

            const optionContainer = document.createElement("div");
            optionContainer.appendChild(optionInput);
            optionContainer.appendChild(optionLabel);

            questionElement.appendChild(optionContainer);
        });

        questionsContainer.appendChild(questionElement);
    });
}

// ... (rest of your code)


function submitQuiz() {
    const userResponses = [];
    const questionElements = document.querySelectorAll('.question');

    questionElements.forEach((questionElement, index) => {
        const selectedOption = questionElement.querySelector(`input[name="question-${index + 1}-options"]:checked`);
        userResponses.push(selectedOption ? selectedOption.value : null);
    });

    const quizScore = calculateQuizResults(shuffledQuestions, userResponses);

    const originalQuestions = shuffleArray(getQuestions(selectedSubject, selectedChapter, selectedMarks));

    const quizResults = {
        score: quizScore,
        totalMarks: selectedMarks,
        questions: originalQuestions.map((question, index) => ({
            question: question.question,
            userAnswer: userResponses[index],
            correctAnswer: question.answer,
        })),
        selectedSubject: selectedSubject,
    };
    console.log("quizResults:", quizResults);
    localStorage.setItem("quizResults", JSON.stringify(quizResults));
    window.location.href = "../RESULT/result.html";
}

function calculateQuizResults(questions, userResponses) {
    let score = 0;

    userResponses.forEach((userAnswer, index) => {
        const correctAnswer = questions[index].answer;

        if (userAnswer === correctAnswer) {
            score++;
        }
    });

    return score;
}

document.addEventListener("DOMContentLoaded", function () {
    updateClock();
    startTimer();

    const backButton = document.getElementById("back-button");
    if (backButton) {
        backButton.addEventListener("click", goBack);
    }

    const submitButton = document.getElementById("submit-button");
    if (submitButton) {
        submitButton.addEventListener("click", submitQuiz);
    }

    const urlParams = new URLSearchParams(window.location.search);
    const selectedSubject = urlParams.get('subject');
    const selectedChapter = urlParams.get('chapter');
    const selectedMarks = parseInt(urlParams.get('marks')) || 1;

    const selectedQuestions = getQuestions(selectedSubject, selectedChapter, selectedMarks);

    displayQuestions(selectedQuestions);
});